UPDATE dbo.Disease SET overview = 'Ischial bursitis(also known asweaver''s bottom) is inflammation of thesynovial bursalocated betweengluteus maximus muscleandischial tuberosity.It is usually caused by prolonged sitting on a hard surface.' WHERE code = 'Q6079234';
